    private void btn1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn1ActionPerformed
                                                                        // For Brute Force Check Matching ...
                                                                       
        String text,pattern;
        
        text=text1.getText();
        pattern=pattern1.getText();
        int n=text.length();
        int m=pattern.length();
        int i=0,j=0,k=0,count=0;
        boolean found=false;
        while(i<n)                                       // To visit every character of Text...
        {
            k=i;
            while(j<m&&k<n)                              // To Match Substring...
            {
       //         System.out.println(" k ="+k);
                
                if(text.charAt(k)==pattern.charAt(j))   // compare text and pattern character by character 
                {
         //           System.out.println(" j "+j);
                    count++;
                    j++;
                    k++;
                }
                else
                {
                    break;
                }           
            }
            
            if(count==m)
             {
                found=true;          // if substirng found then break
                break;
            }
            count=0;
            j=0;    
            i++;
        }
        if(found)
        {
            Output1.setText("Pattern Matched at index "+i);
        }
        else
        {
            Output1.setText("Pattern Not Matched !!!");
    }
        
    }//GEN-LAST:event_btn1ActionPerformed

