    static int KMP(String patten, String txt)
    {
        int patten_length = patten.length();
        int txt_length = txt.length();
 
        int array[] = new int[patten_length];
        int j = 0;  
 
        makearray(patten,patten_length,array);
        boolean found=false;
        int i = 0,ans=-1;  
        while (i < txt_length)
        {
            if (patten.charAt(j) == txt.charAt(i))
            {
                j++;
                i++;
            }
            if (j == patten_length)
            {
             
            //    System.out.println("Found pattern "+"at index " + (i-j));
                
                 ans=i-j;
                j = array[j-1];
                break;
            }
            else if (i < txt_length && patten.charAt(j) != txt.charAt(i))
            {
                if (j != 0)
                    j = array[j-1];
                else
                    i = i+1;
            }
        }
       
       //    System.out.println(" chec "+ans);
            return ans;
    }
      static void makearray(String patten, int patten_lenght, int array[])
    {
        int l = 0;
        int i = 1;
        array[0] = 0;  
        while (i < patten_lenght)
        {
            if (patten.charAt(i) == patten.charAt(l))
            {
                l++;
                array[i] = l;
                i++;
            }
            else  
            {
                if (l != 0)
                {
                    l = array[l-1];
                }
                else  
                {
                    array[i] = l;
                    i++;
                }
            }
        }
    }
    
    
    private void btn2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn2ActionPerformed
                                                           // KMP match button
        String text=text2.getText();
        String pattern=pattern2.getText();                 
        int result=KMP( pattern,text);                     
        if(result!=-1)
            Output2.setText("Pattern Matched at index "+result);
        else
            Output2.setText("Pattern Not Matched !");
    }//GEN-LAST:event_btn2ActionPerformed

    
    
    
    
